import{_ as s,c as a,o as n,a as e}from"./app.92446402.js";const C=JSON.parse('{"title":"\u{1F525} (#72) The CSS Unselector","description":"","frontmatter":{"title":"\u{1F525} (#72) The CSS Unselector","head":[["link",{"rel":"canonical","href":"https://michaelnthiessen.com/weekly-072-august-03/"}]]},"headers":[{"level":2,"title":"\u{1F525} The CSS Unselector","slug":"\u{1F525}-the-css-unselector"},{"level":2,"title":"\u{1F525} \u52A8\u6001\u8FD4\u56DE\u503C","slug":"\u{1F525}-\u52A8\u6001\u8FD4\u56DE\u503C"},{"level":2,"title":"\u{1F4DC} \u5931\u6548\u7684\u8BA1\u7B97\u5C5E\u6027 Computed","slug":"\u{1F4DC}-\u5931\u6548\u7684\u8BA1\u7B97\u5C5E\u6027-computed"},{"level":2,"title":"\u{1F4AC} \u7406\u8BBA\u548C\u5B9E\u8DF5","slug":"\u{1F4AC}-\u7406\u8BBA\u548C\u5B9E\u8DF5"},{"level":2,"title":"\u{1F9E0} \u95F4\u9694\u91CD\u590D\uFF1A\u4E3A\u4EC0\u4E48\u5FC5\u987B\u6709\u4E00\u4E2A\u771F\u76F8\u7684\u6765\u6E90","slug":"\u{1F9E0}-\u95F4\u9694\u91CD\u590D\uFF1A\u4E3A\u4EC0\u4E48\u5FC5\u987B\u6709\u4E00\u4E2A\u771F\u76F8\u7684\u6765\u6E90"},{"level":2,"title":"\u6765\u6E90","slug":"\u6765\u6E90"}],"relativePath":"072.md"}'),o={name:"072.md"},l=e(`<h1 id="\u{1F525}-72-the-css-unselector" tabindex="-1">\u{1F525} (#72) The CSS Unselector <a class="header-anchor" href="#\u{1F525}-72-the-css-unselector" aria-hidden="true">#</a></h1><p>August 2022</p><p>\u5929\u54EA\uFF0C\u867D\u7136\u5DF2\u7ECF\u6301\u7EED\u53D1\u5E03\u4E86 71 \u5468\u7684\u5468\u520A\uFF0C\u6211\u4ECA\u5929\u5DEE\u70B9\u5FD8\u4E86\uFF01</p><p>\u65B0\u5468\u520A\u53D1\u4E86\uFF0C\u867D\u8FDF\u4F46\u5230\uFF01</p><p>\u8FD9\u662F\u672C\u5468\u7684 tips \u5185\u5BB9\uFF01</p><p>\u2014 Michael</p><h2 id="\u{1F525}-the-css-unselector" tabindex="-1">\u{1F525} The CSS Unselector <a class="header-anchor" href="#\u{1F525}-the-css-unselector" aria-hidden="true">#</a></h2><p>\u63D0\u8D77 CSS \u6211\u4EEC\u901A\u5E38\u8BA8\u8BBA\u7684\u662F\u5982\u4F55\u901A\u8FC7\u9009\u62E9\u5668\u6765\u9009\u62E9\u5177\u4F53\u5143\u7D20\u3002</p><p>\u8981\u786E\u4FDD\u9009\u5BF9\u4E86\u5143\u7D20\uFF0C\u8FD9\u5F88\u96BE\u3002</p><p>\u6211\u7684\u535A\u5BA2\u4F7F\u7528\u4E86 Vue Router \u63D0\u4F9B\u7684 <code>.router-link-active</code> \u6765\u8BA9\u94FE\u63A5\u5F53\u524D\u7684\u9875\u9762\u3002\u6211\u4F1A\u6539\u53D8\u5F53\u524D\u94FE\u63A5\u7684\u989C\u8272\uFF0C\u8BA9\u7528\u6237\u77E5\u9053\u6211\u4EEC\u5728\u54EA\u3002</p><p>But I wanted to make sure that all links <em>within</em> the page do not get highlighted in the same way. \u6211\u60F3\u786E\u4FDD\u9875\u9762\u91CC\u7684\u94FE\u63A5</p><p>For example, a table of contents at the beginning of an article should have normal looking links, not the changed colour.</p><p>To do this I needed to avoid selecting all of the links with a <code>#</code> in the <code>href</code> attribute:</p><div class="language-css"><span class="copy"></span><pre><code><span class="line"><span style="color:#89DDFF;">.</span><span style="color:#FFCB6B;">router-link-active</span><span style="color:#A6ACCD;">: not(</span><span style="color:#89DDFF;">[</span><span style="color:#C792EA;">href</span><span style="color:#A6ACCD;"> * </span><span style="color:#89DDFF;">=</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">#</span><span style="color:#89DDFF;">&quot;</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">]</span><span style="color:#A6ACCD;">);</span></span>
<span class="line"></span></code></pre></div><p>I\u2019m using a <code>:not</code> query, but then using an attribute selector inside of that.</p><p>My actual code is even more fun, because there are a few other things going on that I need to account for:</p><div class="language-css"><span class="copy"></span><pre><code><span class="line"><span style="color:#89DDFF;">.</span><span style="color:#FFCB6B;">nav-link</span><span style="color:#89DDFF;">.</span><span style="color:#FFCB6B;">router-link-exact-active</span><span style="color:#89DDFF;">:</span><span style="color:#C792EA;">not</span><span style="color:#89DDFF;">(.</span><span style="color:#FFCB6B;">site-title</span><span style="color:#89DDFF;">):</span><span style="color:#C792EA;">not</span><span style="color:#89DDFF;">([</span><span style="color:#C792EA;">href</span><span style="color:#89DDFF;">*=</span><span style="color:#89DDFF;">&#39;</span><span style="color:#C3E88D;">#</span><span style="color:#89DDFF;">&#39;</span><span style="color:#89DDFF;">])</span></span>
<span class="line"></span></code></pre></div><h2 id="\u{1F525}-\u52A8\u6001\u8FD4\u56DE\u503C" tabindex="-1">\u{1F525} \u52A8\u6001\u8FD4\u56DE\u503C <a class="header-anchor" href="#\u{1F525}-\u52A8\u6001\u8FD4\u56DE\u503C" aria-hidden="true">#</a></h2><p>\u7EC4\u5408\u4EE3\u7801\u53EF\u4EE5\u8FD4\u56DE\u5355\u4E2A\u503C\u6216\u8005\u5BF9\u8C61\u3002\u901A\u5E38\uFF0C\u4F7F\u7528 <code>ref</code> \u5305\u88F9\u3002</p><p>\u4F46\u6211\u4EEC\u53EF\u4EE5\u6839\u636E\u9700\u8981\u52A8\u6001\u5207\u6362\uFF1A</p><div class="language-javascript"><span class="copy"></span><pre><code><span class="line"><span style="color:#676E95;font-style:italic;">// \u8FD4\u56DE\u5355\u4E2A\u503C</span></span>
<span class="line"><span style="color:#C792EA;">const</span><span style="color:#A6ACCD;"> now </span><span style="color:#89DDFF;">=</span><span style="color:#A6ACCD;"> </span><span style="color:#82AAFF;">useNow</span><span style="color:#A6ACCD;">()</span><span style="color:#89DDFF;">;</span></span>
<span class="line"></span>
<span class="line"><span style="color:#676E95;font-style:italic;">// \u66F4\u7CBE\u7EC6\u63A7\u5236</span></span>
<span class="line"><span style="color:#C792EA;">const</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">{</span><span style="color:#A6ACCD;"> now</span><span style="color:#89DDFF;">,</span><span style="color:#A6ACCD;"> pause</span><span style="color:#89DDFF;">,</span><span style="color:#A6ACCD;"> resume </span><span style="color:#89DDFF;">}</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">=</span><span style="color:#A6ACCD;"> </span><span style="color:#82AAFF;">useNow</span><span style="color:#A6ACCD;">(</span><span style="color:#89DDFF;">{</span><span style="color:#A6ACCD;"> </span><span style="color:#F07178;">controls</span><span style="color:#89DDFF;">:</span><span style="color:#A6ACCD;"> </span><span style="color:#FF9CAC;">true</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">}</span><span style="color:#A6ACCD;">)</span><span style="color:#89DDFF;">;</span></span>
<span class="line"></span></code></pre></div><p>\u8FD9\u5F88\u68D2\uFF0C\u56E0\u4E3A\u5927\u591A\u6570\u60C5\u51B5\u6211\u4EEC\u53EA\u9700\u8981\u4E00\u4E2A\u503C\u3002\u4EC0\u4E48\u60C5\u51B5\u4E0B\u4F1A\u53D8\u5F97\u590D\u6742\u5462\uFF1F</p><p>\u901A\u8FC7\u52A8\u6001\u63D0\u4F9B <code>ref</code> \u5BF9\u8C61\u503C\uFF0C\u53EF\u4EE5\u8BA9\u6211\u4EEC\u66F4\u9AD8\u7EA7\u5730\u4F7F\u7528\u4ED6\u4EEC\uFF0C\u5373\u4FBF\u4ED6\u4EEC\u5F88\u5C11\u88AB\u4F7F\u7528\u3002</p><p>\u5185\u90E8\u53EF\u80FD\u8FD9\u6837\u5B9E\u73B0\uFF1A</p><div class="language-javascript"><span class="copy"></span><pre><code><span class="line"><span style="color:#89DDFF;font-style:italic;">export</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;font-style:italic;">default</span><span style="color:#A6ACCD;"> </span><span style="color:#82AAFF;">useNow</span><span style="color:#A6ACCD;">(opts) </span><span style="color:#89DDFF;">{</span></span>
<span class="line"><span style="color:#A6ACCD;">  const {</span></span>
<span class="line"><span style="color:#A6ACCD;">    controls </span><span style="color:#89DDFF;">=</span><span style="color:#A6ACCD;"> </span><span style="color:#FF9CAC;">false</span><span style="color:#89DDFF;">,</span></span>
<span class="line"><span style="color:#A6ACCD;">	</span><span style="color:#89DDFF;">}</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">=</span><span style="color:#A6ACCD;"> opts</span><span style="color:#89DDFF;">;</span></span>
<span class="line"></span>
<span class="line"><span style="color:#89DDFF;">	</span><span style="color:#676E95;font-style:italic;">// Do some things in your composable</span></span>
<span class="line"></span>
<span class="line"><span style="color:#A6ACCD;">  </span><span style="color:#89DDFF;font-style:italic;">if</span><span style="color:#A6ACCD;"> (controls) </span><span style="color:#89DDFF;">{</span></span>
<span class="line"><span style="color:#F07178;">    </span><span style="color:#89DDFF;font-style:italic;">return</span><span style="color:#F07178;"> </span><span style="color:#89DDFF;">{</span><span style="color:#F07178;"> </span><span style="color:#A6ACCD;">now</span><span style="color:#89DDFF;">,</span><span style="color:#F07178;"> </span><span style="color:#A6ACCD;">pause</span><span style="color:#89DDFF;">,</span><span style="color:#F07178;"> </span><span style="color:#A6ACCD;">resume</span><span style="color:#F07178;"> </span><span style="color:#89DDFF;">};</span></span>
<span class="line"><span style="color:#F07178;">  </span><span style="color:#89DDFF;">}</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;font-style:italic;">else</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">{</span></span>
<span class="line"><span style="color:#F07178;">    </span><span style="color:#89DDFF;font-style:italic;">return</span><span style="color:#F07178;"> </span><span style="color:#A6ACCD;">now</span><span style="color:#89DDFF;">;</span></span>
<span class="line"><span style="color:#F07178;">  </span><span style="color:#89DDFF;">}</span></span>
<span class="line"><span style="color:#A6ACCD;">}</span></span>
<span class="line"></span></code></pre></div><h2 id="\u{1F4DC}-\u5931\u6548\u7684\u8BA1\u7B97\u5C5E\u6027-computed" tabindex="-1">\u{1F4DC} \u5931\u6548\u7684\u8BA1\u7B97\u5C5E\u6027 Computed <a class="header-anchor" href="#\u{1F4DC}-\u5931\u6548\u7684\u8BA1\u7B97\u5C5E\u6027-computed" aria-hidden="true">#</a></h2><p>\u4F60\u6709\u6CA1\u6709\u9047\u5230\u8981\u5F3A\u5236\u66F4\u65B0\u8BA1\u7B97\u5C5E\u6027\u7684\u60C5\u51B5\uFF1F\u6709\u6CA1\u6709\u60F3\u8FC7\u4E3A\u5565\u4F1A\u9047\u5230\u8FD9\u79CD\u60C5\u51B5\uFF1F</p><p>\u5728\u8FD9\u7BC7\u6587\u7AE0\u91CC\uFF0CAbdelrahman \u5C55\u793A\u4E86\u4E00\u79CD\u975E\u5E38\u806A\u660E\u7684\u65B9\u6CD5\uFF0C\u6765\u89E3\u51B3\u8FD9\u4E2A\u95EE\u9898\u3002</p><p>\u8FDB\u4E00\u6B65\u9605\u8BFB\uFF1A<a href="https://logaretm.com/blog/2021-01-31-invalidating-computed" target="_blank" rel="noopener noreferrer">Invalidating Computed with the Composition API</a></p><h2 id="\u{1F4AC}-\u7406\u8BBA\u548C\u5B9E\u8DF5" tabindex="-1">\u{1F4AC} \u7406\u8BBA\u548C\u5B9E\u8DF5 <a class="header-anchor" href="#\u{1F4AC}-\u7406\u8BBA\u548C\u5B9E\u8DF5" aria-hidden="true">#</a></h2><p>\u201C\u7406\u8BBA\u662F\u4F60\u77E5\u9053\u4E00\u4E9B\u4E1C\u897F\uFF0C\u4F46\u4E0D\u4F1A\u4F7F\u7528\u3002\u5B9E\u8DF5\u662F\u4F60\u77E5\u9053\u67D0\u4E9B\u4E1C\u897F\u8D77\u4F5C\u7528\uFF0C\u4F46\u4E0D\u77E5\u9053\u4E3A\u4EC0\u4E48\u3002\u7A0B\u5E8F\u5458\u4EEC\u4F1A\u7EDF\u4E00\u7406\u8BBA\u548C\u5B9E\u8DF5\uFF1A\u7A0B\u5E8F\u8DD1\u4E0D\u8D77\u6765\uFF0C\u4E5F\u4E0D\u77E5\u9053\u4E3A\u4EC0\u4E48\u201D \u2014Unknown</p><h2 id="\u{1F9E0}-\u95F4\u9694\u91CD\u590D\uFF1A\u4E3A\u4EC0\u4E48\u5FC5\u987B\u6709\u4E00\u4E2A\u771F\u76F8\u7684\u6765\u6E90" tabindex="-1">\u{1F9E0} \u95F4\u9694\u91CD\u590D\uFF1A\u4E3A\u4EC0\u4E48\u5FC5\u987B\u6709\u4E00\u4E2A\u771F\u76F8\u7684\u6765\u6E90 <a class="header-anchor" href="#\u{1F9E0}-\u95F4\u9694\u91CD\u590D\uFF1A\u4E3A\u4EC0\u4E48\u5FC5\u987B\u6709\u4E00\u4E2A\u771F\u76F8\u7684\u6765\u6E90" aria-hidden="true">#</a></h2><p><strong>\u60F3\u8981\u957F\u65F6\u95F4\u7262\u7262\u8BB0\u4F4F\u67D0\u4E9B\u4E8B\u60C5\uFF0C\u6700\u597D\u65B9\u5F0F\u5C31\u662F\u5B9A\u671F\u56DE\u987E\uFF0C\u9010\u6E10\u589E\u52A0\u56DE\u987E\u7684\u95F4\u9694 \u{1F468}\u200D\u{1F52C}</strong></p><p><strong>\u5B9E\u9645\u4E0A\uFF0C\u8BB0\u4F4F\u8FD9\u4E9B tips \u6BD4\u53D1\u5446\u6709\u7528\u5F97\u591A\uFF0C\u8FD9\u662F\u51E0\u5468\u524D\u7684\u4E00\u4E2A tip \u53EF\u4EE5\u5524\u8D77\u4F60\u7684\u8BB0\u5FC6\u3002</strong></p><p>\u8FD9\u662F\u6211\u7406\u89E3\u7684\u72B6\u6001\u7BA1\u7406\u5173\u952E\u539F\u5219\uFF1A</p><p><strong>Each piece of state has a single owner, a single source of truth.</strong></p><p><strong>\u6BCF\u4E2A\u90E8\u5206\u7684\u72B6\u6001\u662F\u72EC\u7ACB\u7684\u3001\u5355\u4E00\u7684\u4FE1\u606F\u6765\u6E90</strong></p><p>\u4E0D\u5141\u8BB8\u5728\u5176\u4ED6\u5730\u65B9\u4FEE\u6539\u72B6\u6001\uFF0C\u667A\u80FD\u662F\u4E34\u65F6\u8C03\u53D6\u5F15\u7528\u3002</p><p>\u5982\u679C Vuex \u5B58\u50A8\u4E86\u4E00\u4E2A\u6570\u7EC4\uFF0C\u90A3\u53EA\u80FD\u5728 Vuex \u91CC\u5B58\u50A8\u548C\u66F4\u65B0\u3002\u5E94\u7528\u4E2D\u5982\u679C\u8981\u4F7F\u7528\u8FD9\u4E2A\u503C\uFF0C\u5FC5\u987B\u4ECE store \u91CC\u8BFB\u53D6\uFF0C\u76F4\u63A5\u8BFB\u53D6\u6216\u8005\u901A\u8FC7\u5176\u4ED6\u7EC4\u4EF6\u8BFB\u53D6\u3002</p><p>\u5982\u679C\u8FD9\u4E2A\u72B6\u6001\u662F\u67D0\u4E2A\u7EC4\u4EF6\u72EC\u6709\uFF0C\u53EA\u6709\u8FD9\u4E2A\u7EC4\u4EF6\u53EF\u4EE5\u4FEE\u6539\u5B83\u3002\u8FD9\u610F\u5473\u7740\u5176\u4ED6\u7EC4\u4EF6\u6BD4\u5982\u901A\u8FC7 <code>emit</code> \u4E8B\u4EF6\u6765\u8FDB\u884C\u64CD\u4F5C\u66F4\u65B0\u3002</p><p>\u4E3A\u4EC0\u4E48\uFF1F</p><p>\u5982\u679C\u4F60\u5141\u8BB8\u5728\u4EFB\u610F\u4F4D\u7F6E\u4FEE\u6539\uFF0C\u4EE3\u7801\u4F1A\u4E00\u56E2\u7CDF\u3002</p><p>\u901A\u8FC7\u5355\u4E00\u7684\u72B6\u6001\u6765\u6E90\uFF0C\u4F60\u53EF\u4EE5\u8F7B\u677E\u7406\u89E3\u53D1\u751F\u4E86\u4EC0\u4E48\u3002</p><p>\u6700\u597D\u7684\u4EE3\u7801\u662F\u6613\u8BFB\u6613\u61C2\u7684\u3002</p><p><em>p.s. I also have two courses: <a href="https://michaelnthiessen.com/reusable-components" target="_blank" rel="noopener noreferrer">Reusable Components</a> and <a href="https://michaelnthiessen.com/clean-components" target="_blank" rel="noopener noreferrer">Clean Components</a></em></p><h2 id="\u6765\u6E90" tabindex="-1">\u6765\u6E90 <a class="header-anchor" href="#\u6765\u6E90" aria-hidden="true">#</a></h2><p>\u539F\u6587 <a href="https://michaelnthiessen.com/weekly-072-august-03/" target="_blank" rel="noopener noreferrer">https://michaelnthiessen.com/weekly-072-august-03/</a></p><p>\u672C\u4F5C\u54C1\u91C7\u7528<a href="http://creativecommons.org/licenses/by-sa/4.0/" target="_blank" rel="noopener noreferrer">\u77E5\u8BC6\u5171\u4EAB\u7F72\u540D-\u76F8\u540C\u65B9\u5F0F\u5171\u4EAB 4.0 \u56FD\u9645\u8BB8\u53EF\u534F\u8BAE</a>\u8FDB\u884C\u8BB8\u53EF\u3002</p>`,48),p=[l];function t(r,c,i,D,y,F){return n(),a("div",null,p)}var d=s(o,[["render",t]]);export{C as __pageData,d as default};
